$(document).ready(function(){$(".nf_category_field input.filter").keyup(function(){var e=$(this).parent().parent(),t=$(this).val(),n=0;$(e).find("label.category").each(function(){if($(this).text().search(new RegExp(t,"i"))<0)$(this).not(".exclude").hide();else{$(this).show();n++}});t?$(e).find("a > span.count").text("("+n+" matches for current filter)"):$(e).find("a > span.count").text("")})});